mapper.select:
select 	/** AdmRolePgmBtnAuth.selectByKey */
		BTN_NO,
		ROLE_ID,
		MNU_PGM_ID,
		cre_usr_id, cre_dt, upd_usr_id, upd_dt
from	ADM_ROLE_PGM_BTN_AUTH
where	1 = 1
and		BTN_NO = #{btnNo}
and		ROLE_ID = #{roleId}
and		MNU_PGM_ID = #{mnuPgmId}

mapper.delete:
delete  /** AdmRolePgmBtnAuth.delete */
ADM_ROLE_PGM_BTN_AUTH
where	1 = 1
and		BTN_NO = #{btnNo}
and		ROLE_ID = #{roleId}
and		MNU_PGM_ID = #{mnuPgmId}
