mapper.select:
select /** mcm.TeamManagement.selectTeamByTeamTpCd */ team.TEAM_ID, team.DELT_STS_FLG, team.PRNT_TEAM_ID, team.TEAM_NM, team.CO_CD, team.TEAM_ST_DT, team.TEAM_END_DT, team.TEAM_DESC, team.TEAM_LVL, team.ORD_BY_NO, team.TEAM_CD, team.TEAM_MGR_ID, (select USR_NM from ADM_USR where USR_ID = team.TEAM_MGR_ID) TEAM_MGR_NM, team.cre_usr_id, to_char(team.cre_dt, 'yyyymmddhh24mmss') as cre_dt, team.upd_usr_id, to_char(team.upd_dt, 'yyyymmddhh24mmss') as upd_dt, (select count(TEAM_ID) from ADM_TEAM_USR where TEAM_ID = team.TEAM_ID and APLY_END_DT is NULL ) countTeam from ADM_TEAM team where 1 = 1
and team.CO_CD = #{coCd}
and team.TEAM_NM = #{teamNm}
and team.TEAM_MGR_ID = #{teamMgrId}
and team.DELT_STS_FLG = #{deltStsFlg}
order by team.TEAM_LVL ASC, team.ORD_BY_NO ASC

select /** sqlMapTeamManagement.selectCompanyMapWithProjectUser */ COM.CO_CD, COM.CO_NM FROM ADM_CO COM, (
SELECT DISTINCT PJT.CO_CD FROM ADM_PJT PJT WHERE 1=1 AND PJT.CO_CD = #{coCd}
SELECT DISTINCT PJT.CO_CD FROM ADM_PJT PJT, ADM_PJT_USR USR WHERE 1=1 AND PJT.CO_CD IS NOT NULL AND PJT.USE_FLG ='Y' AND USR.USR_ID = #{usrId} AND USR.PJT_ID = PJT.PJT_ID
)PJT WHERE 1 = 1 AND COM.CO_CD = PJT.CO_CD ORDER BY COM.CO_NM

/** sqlMapTeamManagement.searchAdmCoForComboboxPlugin */ SELECT DISTINCT TMP.CO_CD, COM.CO_NM FROM ( SELECT DISTINCT SLS_CTRT_CO.CO_CD FROM ADM_PJT_ITRTN , SLS_CTRT_CO WHERE 1 = 1
AND ADM_PJT_ITRTN.PJT_ID = #{pjtId}
AND ADM_PJT_ITRTN.CTRT_ID = SLS_CTRT_CO.CTRT_ID UNION ALL SELECT COM.CO_CD FROM ADM_PJT, ADM_CO COM WHERE 1 = 1
AND ADM_PJT.PJT_ID = #{pjtId}
AND COM.CO_CD = ADM_PJT.CO_CD
UNION ALL SELECT CO_CD FROM ADM_CO WHERE 1 = 1 AND CO_CD = #{coCd}
) TMP, ADM_CO COM WHERE 1 = 1
AND TMP.CO_CD = #{coCd}
AND TMP.CO_CD = COM.CO_CD

/** TeamManagement.selectTeamAllocationXML */
SELECT TEAM_ID AS "teamId", PRNT_TEAM_ID AS "prntTeamId", TEAM_NM AS "teamNm", CO_CD AS "coCd", TEAM_DESC AS "teamDesc", TEAM_LVL AS "teamLvl", ORD_BY_NO AS "ordByNo", TEAM_CD AS "teamCd", TEAM_MGR_ID AS "teamMgrId", TEAM_MGR_NM AS "teamMgrNm", FULL_NM AS "fullNm", LEVEL AS "level" FROM (SELECT TEAM_ID, NVL(PRNT_TEAM_ID,0) PRNT_TEAM_ID, TEAM_NM, CO_CD, NVL(TEAM_DESC,'') TEAM_DESC, TEAM_LVL, ORD_BY_NO, TEAM_CD, TEAM_MGR_ID, (SELECT USR_NM FROM ADM_USR WHERE USR_ID = TEAM_MGR_ID ) TEAM_MGR_NM, TEAM_CD || '(' || TEAM_NM || ')' AS FULL_NM FROM ADM_TEAM WHERE 1 = 1
AND CO_CD = #{coCd}
AND DELT_STS_FLG = 'N' )ADM_TEAM_FILTER START WITH ADM_TEAM_FILTER.PRNT_TEAM_ID = '0' CONNECT BY NOCYCLE PRIOR ADM_TEAM_FILTER.TEAM_ID = ADM_TEAM_FILTER.PRNT_TEAM_ID ORDER SIBLINGS BY ORD_BY_NO
SELECT TEAM_ID AS "teamId", PRNT_TEAM_ID AS "prntTeamId", TEAM_NM AS "teamNm", CO_CD AS "coCd", TEAM_ST_DT AS "teamStDt", TEAM_END_DT AS "teamEndDt", TEAM_DESC AS "teamDesc", TEAM_LVL AS "teamLvl", ORD_BY_NO AS "ordByNo", TEAM_CD AS "teamCd", TEAM_MGR_ID AS "teamMgrId", TEAM_MGR_NM AS "teamMgrNm", FULL_NM AS "fullNm", LEVEL AS "level" FROM (SELECT TEAM.TEAM_ID, NVL(TEAM.PRNT_TEAM_ID,0) PRNT_TEAM_ID, TEAM_NM, CO_CD, TO_CHAR(TEAM_ST_DT, 'yyyy-mm-dd') TEAM_ST_DT, TO_CHAR(TEAM_END_DT, 'yyyy-mm-dd') TEAM_END_DT, NVL(TEAM_DESC,'') TEAM_DESC, TEAM_LVL, ORD_BY_NO, TEAM_CD, TEAM_MGR_ID, (SELECT USR_NM FROM ADM_USR WHERE USR_ID = TEAM_MGR_ID ) TEAM_MGR_NM, TEAM.TEAM_CD || '(' || TEAM.TEAM_NM || ')' AS FULL_NM FROM ADM_TEAM TEAM WHERE 1 = 1
AND CO_CD = #{coCd}
AND TEAM.DELT_STS_FLG = 'N' )ADM_TEAM_FILTER START WITH ADM_TEAM_FILTER.PRNT_TEAM_ID = '0' CONNECT BY NOCYCLE PRIOR ADM_TEAM_FILTER.TEAM_ID = ADM_TEAM_FILTER.PRNT_TEAM_ID ORDER SIBLINGS BY ORD_BY_NO

select adm_team_usr.APRO_OWNR_FLG
from ADM_TEAM_USR adm_team_usr
where USR_ID = #{oldUsrId}
and TEAM_ID = #{teamId}

mapper.delete:
delete
from adm_team_usr
where USR_ID = #{oldUsrId}
  and TEAM_ID = #{teamId}

mapper.select:
select /** TeamManagement.checkExistUsr */
    COUNT(*)
from ADM_TEAM_USR
where 1 = 1
  and TEAM_ID = #{teamId}
  and USR_ID = #{usrId}

/** TeamManagement.getNumberOfMembers */
SELECT CASE
           WHEN (
                    SELECT apro_ownr_flg
                    FROM adm_team_usr
                    WHERE usr_id = (
                        SELECT team_mgr_id
                        FROM adm_team
                        WHERE team_id = #{teamId}
                    )
                      AND team_id = #{teamId}
                ) = 'Y' THEN (
               SELECT COUNT(*)
               FROM adm_team_usr
               WHERE team_id = #{teamId}
                 AND apro_ownr_flg = 'Y'
                 AND usr_id IN (
                   SELECT usr_id
                   FROM adm_usr
                   WHERE adm_usr.act_flg = 'Y'
               )
           )
           ELSE (SELECT COUNT(*) + 1
                 FROM adm_team_usr
                 WHERE team_id = #{teamId}
                   AND apro_ownr_flg = 'Y'
                   AND usr_id IN (
                     SELECT usr_id
                     FROM adm_usr
                     WHERE adm_usr.act_flg = 'Y'
                 ))
           END
FROM dual

/**[SQLID: TeamManagementMapper.selectListTeamByTeamMgrId]*/ WITH tbl_all AS ( SELECT team_id, prnt_team_id, team_nm, co_cd, team_st_dt, team_end_dt, team_desc, ord_by_no, team_cd, team_mgr_id, team_mgr_nm, full_nm, level team_lvl, LPAD(' â”— ', 2 * level - 1, 's/') || TEAM_NM as TEAM_NM_2 FROM ( SELECT team.team_id, nvl(team.prnt_team_id, 0) prnt_team_id, team_nm, co_cd, TO_CHAR(team_st_dt, 'yyyy-mm-dd') team_st_dt, TO_CHAR(team_end_dt, 'yyyy-mm-dd') team_end_dt, nvl(team_desc, '') team_desc, team_lvl, ord_by_no, team_cd, team_mgr_id, ( SELECT usr_nm FROM adm_usr WHERE usr_id = team_mgr_id ) team_mgr_nm, team.team_cd || '(' || team.team_nm || ')' AS full_nm FROM adm_team team WHERE 1 = 1 AND team.co_cd = #{coCd} AND team.delt_sts_flg = 'N' ) adm_team_filter START WITH adm_team_filter.prnt_team_id = '0' CONNECT BY NOCYCLE PRIOR adm_team_filter.team_id = adm_team_filter.prnt_team_id ORDER SIBLINGS BY ord_by_no ), tbl_filter AS ( SELECT team_id, team_nm FROM adm_team team WHERE 1 = 1 AND team.co_cd = #{coCd} AND team.delt_sts_flg = 'N'
AND team.TEAM_ID not in ( SELECT tteam.TEAM_ID FROM ADM_TEAM tteam WHERE 1 = 1 AND tteam.co_cd = #{coCd} AND tteam.delt_sts_flg = 'N' AND tteam.TEAM_MGR_ID != #{mgrId} AND EXISTS ( SELECT * FROM ADM_TEAM tteam2 WHERE tteam2.TEAM_ID = tteam.PRNT_TEAM_ID AND tteam2.TEAM_MGR_ID != #{mgrId} AND tteam2.co_cd = #{coCd} AND tteam2.delt_sts_flg = 'N' ) AND NOT EXISTS ( SELECT * FROM ADM_TEAM tteam3 WHERE 1 = 1 AND tteam3.TEAM_MGR_ID = #{mgrId} AND tteam3.co_cd = #{coCd} AND tteam3.delt_sts_flg = 'N' START WITH tteam3.TEAM_ID = tteam.PRNT_TEAM_ID CONNECT BY PRIOR tteam3.PRNT_TEAM_ID = tteam3.TEAM_ID ) AND NOT EXISTS ( SELECT * FROM ADM_TEAM_USR t_usr WHERE t_usr.TEAM_ID = tteam.TEAM_ID AND t_usr.USR_ID = #{mgrId} ) )
START WITH team.team_id IN (
SELECT TTEAM.TEAM_ID FROM ADM_TEAM_USR TUSR, ADM_TEAM TTEAM WHERE TTEAM.TEAM_ID = TUSR.TEAM_ID
AND TUSR.USR_ID = #{mgrId}
) AND team.prnt_team_id NOT IN (
SELECT TTEAM.TEAM_ID FROM ADM_TEAM_USR TUSR, ADM_TEAM TTEAM WHERE TTEAM.TEAM_ID = TUSR.TEAM_ID
AND TUSR.USR_ID = #{mgrId}
) CONNECT BY PRIOR team.team_id = team.prnt_team_id ORDER SIBLINGS BY ord_by_no ) SELECT tbl_all.team_id , tbl_all.co_cd , tbl_all.team_nm , tbl_all.team_mgr_id , tbl_all.prnt_team_id , tbl_all.team_lvl, tbl_all.ord_by_no, tbl_all.team_nm_2 FROM tbl_all INNER JOIN tbl_filter ON ( tbl_filter.team_id = tbl_all.team_id )

