AND CO_CD = #{coCd}
AND TEAM_ID !=#{startId} AND TEAM_ID not in ( SELECT TEAM_ID FROM ADM_TEAM START WITH TEAM_ID =#{startId} CONNECT BY PRIOR TEAM_ID = PRNT_TEAM_ID) AND DELT_STS_FLG = 'N' )ADM_TEAM_FILTER WHERE TEAM_ID !=#{startPrntTeamId} START WITH ADM_TEAM_FILTER.PRNT_TEAM_ID = '0' CONNECT BY NOCYCLE PRIOR ADM_TEAM_FILTER.TEAM_ID = ADM_TEAM_FILTER.PRNT_TEAM_ID ORDER SIBLINGS BY ORD_BY_NO
