mapper.update:
update /** TeamUserHistory.updateApplyEndDate */
    ADM_TEAM_USR_HIS
set APLY_END_DT = sysdate,
    WRK_FLG     = 'N',
    upd_usr_id  = #{updateUser},
    upd_dt      = sysdate
where 1 = 1
  and TEAM_ID = #{teamId}
  and USR_ID = #{usrId}
  and APRO_OWNR_FLG = #{aproOwnrFlg}

mapper.select:
select /** TeamUserHistory.getNumberOfTeamById */
    COUNT(*)
from ADM_TEAM_USR
where 1 = 1
  and USR_ID = #{usrId}
  and TEAM_ID = #{teamId}
  and APRO_OWNR_FLG = #{ownerFlg}

mapper.update:
update /** TeamUserHistory.updateAproOnwrFlg */
    ADM_TEAM_USR_HIS
set APRO_OWNR_FLG = #{aproOwnrFlg},
    upd_usr_id    = #{updateUser},
    upd_dt        = sysdate
where 1 = 1
    and USR_ID = #{usrId} 
    and TEAM_ID = #{teamId}
    and APLY_ST_DT  = #{aplyStDt}
